package day16;

public class SwapNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 5;
		int b = 10;
		int c;
		System.out.println("Value of a is: "+ a + "\n" +"Value of b is:  "+b);
		
		c= b -a;

		a+=c;
		b=a-5;
		System.out.println("Value of a is: "+ a + "\n" +"Value of b is:  "+b);
		
		

	}

}
